import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default class Addnewhope extends Component {
    
    constructor(props) {
        super(props);

        this.onChangeHopeHeader = this.onChangeHopeHeader.bind(this);
        this.onChangeHopeBody = this.onChangeHopeBody.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            Hope_header: '',
            Hope_body: '',
            date: new Date()
        }
    }
    onChangeHopeHeader(e) {
        this.setState({
            Hope_header: e.target.value
        });
    }

    onChangeHopeBody(e) {
        this.setState({
            Hope_body: e.target.value
        });
    }
    onChangeDate(date) {
        this.setState({
            date: date
        });
    }
    onSubmit(e) {
        e.preventDefault();

        const NewHope = {
            Hope_header: this.state.Hope_header,
            Hope_body: this.state.Hope_body,
            date: this.state.date
        }
    }


    render() {
        return (
            <div style={{ marginTop: 10 }}>
                <h3>Add New Hope</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Header: </label>
                        <input type="text"
                            className="form-control"
                            value={this.state.Hope_header}
                            onChange={this.onChangeHopeHeader}
                        />
                    </div>
                    <div className="form-group">
                        <label>Responsible: </label>
                        <input
                            type="text"
                            className="form-control"
                            value={this.state.todo_responsible}
                            onChange={this.onChangeTodoResponsible}
                        />
                    </div>
                    <div className="form-group">
                        <div className="form-check form-check-inline">
                            <input className="form-check-input"
                                type="radio"
                                name="priorityOptions"
                                id="priorityLow"
                                value="Low"
                                checked={this.state.todo_priority === 'Low'}
                                onChange={this.onChangeTodoPriority}
                            />
                            <label className="form-check-label">Low</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input className="form-check-input"
                                type="radio"
                                name="priorityOptions"
                                id="priorityMedium"
                                value="Medium"
                                checked={this.state.todo_priority === 'Medium'}
                                onChange={this.onChangeTodoPriority}
                            />
                            <label className="form-check-label">Medium</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input className="form-check-input"
                                type="radio"
                                name="priorityOptions"
                                id="priorityHigh"
                                value="High"
                                checked={this.state.todo_priority === 'High'}
                                onChange={this.onChangeTodoPriority}
                            />
                            <label className="form-check-label">High</label>
                        </div>
                    </div>                    <div className="form-group">
                        <input type="submit" value="Create Todo" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
