import React, { Component } from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import axios from 'axios';

export default class Addnewhope extends Component {
    
    constructor(props) {
        super(props);

        this.onChangeHopeHeader = this.onChangeHopeHeader.bind(this);
        this.onChangeHopeBody = this.onChangeHopeBody.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            Blog_header: '',
            Blog_body: '',
            date: new Date()
        }
    }
    onChangeHopeHeader(e) {
        this.setState({
            Blog_header: e.target.value
        });
    }

    onChangeHopeBody(e) {
        this.setState({
            Blog_body: e.target.value
        });
    }
    onChangeDate(date) {
        this.setState({
            date: date
        });
    }
    onSubmit(e) {
        e.preventDefault();

        const NewHope = {
            Blog_header: this.state.Blog_header,
            Blog_body: this.state.Blog_body,
/*          date: this.state.date,
*/
           };

        console.log(NewHope);

        axios.post('http://localhost:4000/add', NewHope)
        .then(res => {console.log(res.data)
            })
        .catch(err => {console.log(err.data)
            });

        this.setState({
            Blog_header: '',
            Blog_body: '',
            date: new Date()
        })
    }


    render() {
        return (
            <div style={{ marginTop: 10 }}>
                <h3>Add New Hope</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Header: </label>
                        <input type="text"
                            className="form-control"
                            value={this.state.Blog_header}
                            onChange={this.onChangeHopeHeader}
                        />
                    </div>
                    <div className="form-group">
                        <label>Body: </label>
                        <input
                            type="text"
                            className="form-control"
                            value={this.state.Blog_body}
                            onChange={this.onChangeHopeBody}
                        />
                    </div>
                    <div className="form-group">
                        <label>Date: </label>
                        <div>
                            <DatePicker
                                selected={this.state.date}
                                onChange={this.onChangeDate}
                            />
                        </div>
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Add New Hope" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
