import React, { Component } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import {getHopes} from '../Redux/actions/hopeActions';

const Blog = props => (
    <tr>
        <td>{props.hope.Blog_Header}</td>
        <td>{props.hope.Blog_Body}</td>
        <td>{props.hope.Blog_Date.substring(0,10)}</td>
    </tr>
)

class Hopeslist extends Component {
    constructor(props) {
        super(props);
        this.state = { hopes: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:4000/')
        .then(response => {
            this.setState({ hopes: response.data });
        })
        .catch(error => {
            console.log(error);
        })
    }

    hopelist() {
        return this.state.hopes.map(function (currenthope, i){
            return <Blog hope={currenthope} key={i} />;
        })
    }
    render() {
        return (
            <div>
                <h3> Hopes List  </h3>
                <table className ='table table-striped' style ={{ marginTop: 20}}>
                    <thead>
                        <tr>
                            <th>Hope Header</th>
                            <th>Hope Body</th>
                            <th>Date Created</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.hopelist() }
                    </tbody>
                </table>
            </div>
        )
    }
}

Hopeslist.PropTypes = {
    getHopes: PropTypes.func.isRequired,
    hopes: PropTypes.object
}
const mapStateToProps = {
    hopes: state.hopes.items,
    errors: state.errors
}

export default connect(
    mapStateToProps,
    { getHopes }
)(Hopeslist);
