import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import {getHopes} from '../Redux/actions/hopeActions';

const Blog = props => (
    <tr>
        <td>{props.hope.Blog_Header}</td>
        <td>{props.hope.Blog_Body}</td>
        <td>{props.hope.Blog_Date.substring(0,10)}</td>
    </tr>
)

class Hopeslist extends Component {
    constructor(props) {
        super(props);
        this.state = { hopes: [] };
    }

    componentDidMount() {
        this.props.getHopes();
    }

    hopelist() {
        return this.props.hopes.map(function (currenthope, i){
            return <Blog hope={currenthope} key={i} />;
        })
    }
    render() {
        return (
            <div>
                <h3> Hopes List  </h3>
                <table className ='table table-striped' style ={{ marginTop: 20}}>
                    <thead>
                        <tr>
                            <th>Hope Header</th>
                            <th>Hope Body</th>
                            <th>Date Created</th>
                        </tr>
                    </thead>
                    <tbody>
                        {hopelist() }
                    </tbody>
                </table>
            </div>
        )
    }
}

Hopeslist.propTypes = {
    getHopes: PropTypes.func.isRequired,
    hopes: PropTypes.object
}
const mapStateToProps = state => ({
    hopes: state.hope.items,
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { getHopes }
)(Hopeslist);
