import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import {getHopes} from '../Redux/actions/hopeActions';


class Hopeslist extends Component {
    constructor(props) {
        super(props);
        this.state = { hopes: [] };
    }

    componentDidMount() {
        this.props.getHopes();
    }

    
    render() {
        const hopelist = this.props.hopes.map(blogs => (
            <div key = {blogs.id}>
            <h3>{blogs.Blog_Header}</h3>
            <p>{blogs.Blog_Body}</p>
            <h4>{blogs.Blog_date.substring(0, 10)}</h4>
            </div>
        ));

        return (
            <div>
                <h3> Hopes List  </h3>
                <table className ='table table-striped' style ={{ marginTop: 20}}>
                    <thead>
                        <tr>
                            <th>Hope Header</th>
                            <th>Hope Body</th>
                            <th>Date Created</th>
                        </tr>
                    </thead>
                    <tbody>
                        { hopelist }
                    </tbody>
                </table>
            </div>
        )
    }
}

Hopeslist.propTypes = {
    getHopes: PropTypes.func.isRequired,
    hopes: PropTypes.object
}
const mapStateToProps = state => ({
    hopes: state.hope.items,
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { getHopes }
)(Hopeslist);
