import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default class Addnewhope extends Component {
    
    constructor(props) {
        super(props);

        this.onChangeHopeHeader = this.onChangeHopeHeader.bind(this);
        this.onChangeHopeBody = this.onChangeHopeBody.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            Hope_header: '',
            Hope_body: '',
            date: new Date()
        }
    }
    onChangeHopeHeader(e) {
        this.setState({
            Hope_header: e.target.value
        });
    }

    onChangeHopeBody(e) {
        this.setState({
            Hope_body: e.target.value
        });
    }
    onChangeDate(date) {
        this.setState({
            date: date
        });
    }
    onSubmit(e) {
        e.preventDefault();

        const NewHope = {
            Hope_header: this.state.Hope_header,
            Hope_body: this.state.Hope_body,
            date: this.state.date,
        }
    }


    render() {
        return (
            <div>
                <p>You are on the add new Hope component!</p>
            </div>
        )
    }
}
