import axios from 'axios';

import {FETCH_HOPES, 
        CREATE_HOPE,
        GET_ERRORS
} from "./types";

//fetch all hopes
export const getHopes = () => dispatch => {
    axios.get('http://localhost:4000/')
        .then(res => {
            let hopes = res.data
            dispatch({
                type: FETCH_HOPES,
                paymoad: hopes
            })
        })
        .catch(err => {
            dispatch({
                type: GET_ERRORS,
                payload: err.res.data
            })
        });
};

//Create Hope

export const createHope = (newHope) => dispatch => {
    axios.post('http://localhost:4000/add', newHope)
        .then(res => res.json())
        .then(post => {
            dispatch({
                type: CREATE_HOPE,
                payload: post
            })
        })
        .catch(err => {
            dispatch({
                type: GET_ERRORS,
                payload: err.res.data
            })
        });
};
