import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Hope = props => {
    <tr>
        <td>{props.hopes.Blog_Header}</td>
        <td>{props.hopes.Blog_Body}</td>
        <td>{props.hopes.Blog_Date.substring(0,10)}</td>
    </tr>
}

export default class Hopeslist extends Component {
    constructor(props) {
        super(props);
        this.state = { hopes: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:4000/')
        .then(response => {
            this.setState({ hopes: response.data });
        })
        .catch(error => {
            console.log(error);
        })
    }

    hopelist() {
        return this.state.hopes.map(function (currenthope, i){
            return <Hope hope={currenthope} key={i} />;
        })
    }
    render() {
        return (
            <div>
                <h3> Hopes List  </h3>
                <table className ='table table-striped' style ={{ marginTop: 20}}>
                    <thead>
                        <tr>
                            <th>Hope Header</th>
                            <th>Hope Body</th>
                            <th>Date Created</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.hopelist()}
                    </tbody>
                </table>
            </div>
        )
    }
}
